mapper.insert:
insert into blog (title, description, author, image_url, blog_type, content)
values (#{title,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, #{imageUrl,jdbcType=VARCHAR},
#{blogType,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}
)

mapper.update:
UPDATE blog
title = #{title,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, author = #{author,jdbcType=VARCHAR}, image_url = #{imageUrl,jdbcType=VARCHAR}, blog_type = #{blogType,jdbcType=INTEGER}, is_active = #{isActive,jdbcType=BOOLEAN}, content = #{content,jdbcType=LONGVARCHAR},
WHERE blog_id=#{blogId, jdbcType=BIGINT}

update blog
set is_active = false
where blog_id = #{blogId,jdbcType=BIGINT}

mapper.select:
select blog_id, title, description, author, imageUrl, blogType, is_active, content, create_time, update_time
from user
where blog_id = #{blogId,jdbcType=VARCHAR}

select blog_id, title, description, author, imageUrl, blogType, is_active, create_time, update_time
from blog
where is_active = true
and blog_type = #{blogType, jdbcType=INTEGER}
order by update_time desc
limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

select blog_id, title, description, author, imageUrl, blogType, is_active, create_time, update_time
from blog
where is_active = true
and blog_type = #{blogType, jdbcType=INTEGER}
and blog_id > #{lastBlogId,jdbcType=BIGINT}
order by update_time desc
limit #{limit,jdbcType=INTEGER}
